var x = 1;
$(document).ready(function () {

    $(".content").hide(); //updated line, removing the #panel ID.
    $('.unpinnedmenu').show();
    $('#tab').toggle(function () { //adding a toggle function to the #tab
        x = 0; //show
        $('.unpinnedmenu').hide();
        $('.pinnedmenu').show();
        $('#panel').show();
        $('.content').fadeIn('slow');
     
    },
   function () { //when the #tab is next cliked
       x = 1; //hide
       $('.unpinnedmenu').show();
       $('.pinnedmenu').hide();
   });


    $("#tab").mouseenter(function () {
        $('#panel').stop().animate({ width: "280px", opacity: 0.90 }, 500, function () {//sliding the #panel to 400px
            $('.content').fadeIn('slow'); //slides the content into view.
        });
    });
    $(".menu-item").mouseenter(function () {
        $('#panel').stop().animate({ width: "280px", opacity: 0.90 }, 500, function () {//sliding the #panel to 400px
            $('.content').fadeIn('slow'); //slides the content into view.
        });
    });
    

    $(".bluebar").mouseenter(function () {
        if (x == 1) {
            $('.content').fadeOut('slow', function () { //fade out the content 
                $('#panel').stop().animate({ width: "0", opacity: 0.90 }, 500); //slide the #panel back to a width of 0
            });
        }

    });

    $("#panel").mouseleave(function () {
        if (x == 1) {
            $('.content').fadeOut('slow', function () { //fade out the content 
                $('#panel').stop().animate({ width: "0", opacity: 0.90 }, 500); //slide the #panel back to a width of 0
            });
        }

    });
});

function SplitByChar() {

    a = document.getElementById('hidAccessibleModules').value.split(',');


    document.getElementById("DASHBOARD").style.display = "none";
    document.getElementById("rates").style.display = "none";
    document.getElementById("TARIFF_NEW").style.display = "none";
    document.getElementById("OCEANTARIFF_NEW").style.display = "none";
    document.getElementById("TRUCKRATES").style.display = "none";
    document.getElementById("TARIFF").style.display = "none";
    document.getElementById("INACTIVETARIFF").style.display = "none";
    document.getElementById("APPROVEDASADHOC").style.display = "none";
    document.getElementById("OCEANRATES").style.display = "none";
    document.getElementById("raterequests").style.display = "none";
    document.getElementById("NEWRATEREQUEST").style.display = "none";

    document.getElementById("NEWAIRRATEREQUEST").style.display = "none";
    document.getElementById("OCEANRATEREQUEST").style.display = "none";
    document.getElementById("TRUCKRATEREQUEST").style.display = "none";
    document.getElementById("fscupdate").style.display = "none";
    document.getElementById("FSCINTLAIRFREIGHTRULES").style.display = "none";
    document.getElementById("FSCUPDATEMALAYSIA").style.display = "none";
    document.getElementById("knowledgebase").style.display = "none";
    document.getElementById("IDENTIFYEUROPEANZONE").style.display = "none";
    document.getElementById("RRAWONLINEMANUAL").style.display = "none";

    document.getElementById("SEARCEHELPDESK").style.display = "none";
    document.getElementById("CHANGEPASSWORD").style.display = "none";
    document.getElementById("USERREPORTS").style.display = "none";
    document.getElementById("ADMINPANEL").style.display = "none";
    document.getElementById("admin").style.display = "none";
    document.getElementById("TARIFFSEARCH").style.display = "none";



    for (i = 0; i <= a.length - 1; i++) {
        try {
            var moduleName = a[i].trim();
            if (document.getElementById(moduleName) != undefined) {
                document.getElementById(moduleName).style.display = "block";
            }
        } catch (e) {
        }
    }
    try {
        if ((document.getElementById("FSCUPDATEMALAYSIA").style.display == "block") || (document.getElementById("FSCINTLAIRFREIGHTRULES").style.display == "block")) {
            document.getElementById("fscupdate").style.display = "block";
        }
    }
    catch (e) {
    }
    try {
        if ((document.getElementById("TARIFF").style.display == "block") || (document.getElementById("INACTIVETARIFF").style.display == "block") || (document.getElementById("APPROVEDASADHOC").style.display == "block") || (document.getElementById("OCEANRATES").style.display == "block") || (document.getElementById("TRUCKRATES").style.display == "block") || (document.getElementById("TARIFF_NEW").style.display == "block") || (document.getElementById("OCEANRATES_NEW").style.display == "block")) {
            document.getElementById("rates").style.display = "block";
        }
    }
    catch (e) {
    }
    try {
        if ((document.getElementById("NEWRATEREQUEST").style.display == "block") || (document.getElementById("NEWAIRRATEREQUEST").style.display == "block") || (document.getElementById("OCEANRATEREQUEST").style.display == "block") || (document.getElementById("TRUCKRATEREQUEST").style.display == "block")) {
            document.getElementById("raterequests").style.display = "block";
        }
    }
    catch (e) {
        alert(e.Message);
    }
    try {
        if ((document.getElementById("identifyeuropeanzone").style.display == "block")) {
            document.getElementById("knowledgebase").style.display = "block";
        }
    }
    catch (e) {
    }

    try {
        if ((document.getElementById("ADMINPANEL").style.display == "block")) {
            document.getElementById("admin").style.display = "block";
        }
    }
    catch (e) {
    }
}

