function btnDelete_onClick() {
    if (trim(document.getElementById("txtComment").value) == "") {
        alert("Please add reason in Comment box to delete this Rate Request.");
        document.getElementById("txtComment").focus();
        return false;
    }

    var r = confirm('Are you sure you want to delete this rate request?');
    if (r == true) {
        DisplayProgress('Deleting rate request...');

        return true;
    }
    else {
        return false;
    }
}
