function selectMe(stripId, me) {
    d = document.getElementById(stripId);
    for (i = 0; i < d.childNodes.length; i++) {
        if (d.childNodes[i].className == 'tab tab_selected') {
            d.childNodes[i].className = 'tab';
        }
    }

    d = document.getElementById('contentsMaster');
    for (i = 0; i < d.childNodes.length; i++) {
        if (d.childNodes[i].id != undefined) {
            if (d.childNodes[i].id.substr(0, 7) == 'content') {
                d.childNodes[i].style.display = 'none';
            }
        }
    }

    me.className = 'tab tab_selected';
    document.getElementById("content" + me.id.slice(3)).style.display = 'block';
}