function AjaxLoad(loadUrl, responseToObject, onLoadCompleteDisplayObject, onLoadHideObject) {
    customOnLoad(onLoadHideObject);

    if (window.XMLHttpRequest) {// code for IE7+, Firefox, Chrome, Opera, Safari
        xmlhttp = new XMLHttpRequest();
    }
    else {// code for IE6, IE5
        xmlhttp = new ActiveXObject("Microsoft.XMLHTTP");
    }

    xmlhttp.onreadystatechange = function () {
        //alert(xmlhttp.readyState);
        if (xmlhttp.readyState == 4 && xmlhttp.status == 200) {
            customOnLoadComplete(responseToObject, onLoadCompleteDisplayObject);
        }
    }
    xmlhttp.open("GET", loadUrl, true);
    xmlhttp.send();
}

function customOnLoad(onLoadHideObject) {
    if (onLoadHideObject != null) {
        document.getElementById(onLoadHideObject).display = 'none';
    }
}

function customOnLoadComplete(responseToObject, onLoadCompleteDisplayObject) {
    if (responseToObject != null) {
        var res = xmlhttp.responseText;
        document.getElementById(responseToObject).innerHTML =  res.slice(res.indexOf('<span id="lblStatus">') + 21, res.indexOf('</span>'));
    }
    if (onLoadCompleteDisplayObject != null) {
        document.getElementById(onLoadCompleteDisplayObject).display = 'block';
    }
}