function GetCurrentDate() {
    var t = new Date();
    return ((t.getMonth() + 1) + '/' + t.getDate() + '/' + t.getFullYear() + ' ' + t.getHours() + ':' + t.getMinutes() + ':' + t.getSeconds());
}

function GetDate(dt) {
    dt = new Date(dt);
    var m = dt.getMonth() + 1;
    var d = dt.getDate();
    var y = dt.getFullYear();

    return (((m < 10) ? ("0" + m) : m) + '/' + ((d < 10) ? ("0" + d) : d) + '/' + y);
}

