$("input[type='text']").focus(function () {
	DisplayOperations();
});

$("#divTariff").scroll(function () {
	if ($("#divTariff").scrollTop() == 0) {
		DisplayOperations();
	}
	else {
		$("#divOperations").hide();
		$("#tdOperations").css("height", "0px");
		$("#divOperationAdjustments").css("height", "100px");
	}
});

function DisplayOperations() {
	$("#divOperations").show();
	$("#tdOperations").css("height", "40px");
	$("#divOperationAdjustments").css("height", "130px");
}